import React, { useState, useEffect } from "react";
import { Network, Alchemy } from "alchemy-sdk";
import { Avatar, Card, Button, Row, Col, Modal, Image, Input } from "antd";
import {
    DollarCircleOutlined,
    CheckCircleOutlined,
    KeyOutlined,
} from "@ant-design/icons";
import { nftData } from "../data/nft.data";
import "./css/Market.css";
const settings = {
    apiKey: "-C7_ur_4s6R0oUnBNGi4qB3XWfv-HsFR",
    network: Network.ETH_MAINNET,
};

const alchemy = new Alchemy(settings);
const { Meta } = Card;

const Market = () => {
    const [NFTInfo, SetNFTInfo] = useState([]);
    const [isLoading, SetIsLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalLoading, setmodalLoading] = useState(false);
    const showModal = () => {
        setIsModalOpen(true);
    };

    const hanleSubmit = () => {
        setIsModalOpen(false);
    };

    const handleCancel = () => {
        setIsModalOpen(false);
    };
    useEffect(() => {
        const GetContractMetadata = async () => {
            var res = [];
            for (let i = 0; i < nftData.length; i++) {
                const element = nftData[i];
                const meta = await alchemy.nft.getContractMetadata(
                    element.webAddress
                );
                res.push(meta);
            }
            SetNFTInfo(res);
            SetIsLoading(false);
        };
        GetContractMetadata();
    }, [isLoading]);
    return (
        <ul className="market__list ">
            {nftData.map((_, index) => (
                <li className="market__item" key={index}>
                    <Card className="market_li_card" hoverable>
                        <Meta
                            avatar={
                                <Avatar
                                    size={64}
                                    src={NFTInfo[index]?.openSea.imageUrl}
                                />
                            }
                        />
                        <Row className="market_li_description">
                            <Row>
                                <p
                                    style={{
                                        display: "inline",
                                        height: 0,
                                        lineHeight: 0,
                                        display: "flex",
                                    }}
                                >
                                    {NFTInfo[index]?.name}
                                    <CheckCircleOutlined
                                        style={{
                                            fontSize: "14px",
                                            color: "#00a186",
                                        }}
                                    />
                                </p>
                            </Row>
                            <Row className="market_description_content">
                                <Col>fool price</Col>
                                <Col>
                                    <p style={{ display: "inline" }}>
                                        NFT in collection:{" "}
                                    </p>
                                    {NFTInfo[index]?.totalSupply}
                                </Col>
                                <Col>in market</Col>
                            </Row>
                        </Row>

                        <Button
                            size="large"
                            type="primary"
                            className="market_li_btn"
                            onClick={showModal}
                        >
                            <DollarCircleOutlined />
                            Borrow
                        </Button>
                    </Card>
                </li>
            ))}
            <Modal
                title="Make request"
                open={isModalOpen}
                onCancel={handleCancel}
                footer={[
                    <Button key="back" onClick={handleCancel}>
                      Cancel
                    </Button>,
                    <Button key="submit" type="primary" loading={modalLoading} onClick={hanleSubmit}>
                      Submit
                    </Button>,

                  ]}
            >
                <div className="request-container">
                    <div className="request-information">
                        <Image
                            width={200}
                            height={200}
                            src="error"
                            fallback="data:image/png;base64,"
                        />
                    </div>

                    <Input
                        size="large"
                        placeholder="NFT tokenId"
                        prefix={<KeyOutlined />}
                    />
                </div>
            </Modal>
        </ul>
    );
};

export default Market;
